catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }

   //Type appropriate comment here, and begin script below
	if( newValue  == 'Procurement' )
		{
			g_form.clearOptions('x_sksw_uk_itsm_u_sr_subttype');
			g_form.addOption('x_sksw_uk_itsm_u_sr_subttype','Hardware purchase','Hardware purchase');
			g_form.addOption('x_sksw_uk_itsm_u_sr_subttype','Corporate Order','Corporate Order');
			g_form.addOption('x_sksw_uk_itsm_u_sr_subttype','Faulty Goods Return','Faulty Goods Return');
			g_form.addOption('x_sksw_uk_itsm_u_sr_subttype','General query','General query');
			g_form.addOption('x_sksw_uk_itsm_u_sr_subttype','Quote request','Quote request');	
			g_form.addOption('x_sksw_uk_itsm_u_sr_subttype','Contract termination','Contract termination');			
			g_form.addOption('x_sksw_uk_itsm_u_sr_subttype','Mobile Admin','Mobile Admin');			
		}
		else if(newValue  == 'Device Leasing' )
		{
			g_form.clearOptions('x_sksw_uk_itsm_u_sr_subttype');
			g_form.addOption('x_sksw_uk_itsm_u_sr_subttype','Device return','Device return');
			g_form.addOption('x_sksw_uk_itsm_u_sr_subttype','Replacement device','Replacement device');	
			g_form.addOption('x_sksw_uk_itsm_u_sr_subttype','General Query','General Query');
			g_form.addOption('x_sksw_uk_itsm_u_sr_subttype','Device Admin','Device Admin');		

		}
	else if(newValue  == 'Asset Mgmt' )
		{
			g_form.clearOptions('x_sksw_uk_itsm_u_sr_subttype');
			g_form.addOption('x_sksw_uk_itsm_u_sr_subttype','Re-Assign Request','Re-Assign Request');
			g_form.addOption('x_sksw_uk_itsm_u_sr_subttype','Lost / Stolen','Lost / Stolen');
			g_form.addOption('x_sksw_uk_itsm_u_sr_subttype','Repair','Repair');
			g_form.addOption('x_sksw_uk_itsm_u_sr_subttype','Bulk Uploads','Bulk Uploads');
			g_form.addOption('x_sksw_uk_itsm_u_sr_subttype','Return Of Asset/s','Return Of Asset/s');
			g_form.addOption('x_sksw_uk_itsm_u_sr_subttype','Disposal','Disposal');
			g_form.addOption('x_sksw_uk_itsm_u_sr_subttype','Return To Lessor','Return To Lessor');					
			g_form.addOption('x_sksw_uk_itsm_u_sr_subttype','Audit','Audit');
			g_form.addOption('x_sksw_uk_itsm_u_sr_subttype','Report Request','Report Request');					



		}
	else if(newValue  == 'Config Mgmt' )
		{
			g_form.clearOptions('x_sksw_uk_itsm_u_sr_subttype');
			g_form.addOption('x_sksw_uk_itsm_u_sr_subttype','Equipment Request','Equipment Request');
			g_form.addOption('x_sksw_uk_itsm_u_sr_subttype','Contract / Warranty Request','Contract / Warranty Request');
			g_form.addOption('x_sksw_uk_itsm_u_sr_subttype','Software Request','Software Request');	
			g_form.addOption('x_sksw_uk_itsm_u_sr_subttype','Decomission','Decomission');
			g_form.addOption('x_sksw_uk_itsm_u_sr_subttype','Repair','Repair');	
				g_form.addOption('x_sksw_uk_itsm_u_sr_subttype','Disposal','Disposal');
			g_form.addOption('x_sksw_uk_itsm_u_sr_subttype','Capability Request','Capability Request');			


		}

}
