function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading) {
      return;
   }
	if(newValue != ''){

	//g_form.clearValue('please_select_functional_areas_access_should_be_added');
	//g_form.clearValue('please_select_functional_areas_access_should_be_removed');

	var myListCollector = g_list.get("please_select_functional_areas_access_should_be_added");
	myListCollector.reset();
	if (g_form.getValue('role') == 'grn'){
	myListCollector.setQuery('type=icon function^parents_filters=grn');
	}
	else if (g_form.getValue('role') == 'finance fbsby'){
	myListCollector.setQuery('type=icon function^parents_filters=finance fbsby');
	}
	else if (g_form.getValue('role') == 'commercial'){
	myListCollector.setQuery('type=icon function^parents_filters=commercial');
	}
	else if (g_form.getValue('role') == 'finance fbsmx'){
	myListCollector.setQuery('type=icon function^parents_filters=finance fbsmx');
	}
	else if (g_form.getValue('role') == 'finance'){
	myListCollector.setQuery('type=icon function^parents_filters=finance');
	}
	else if (g_form.getValue('role') == 'superuser finance'){
	myListCollector.setQuery('type=icon function^parents_filters=superuser finance');
	}
	else if (g_form.getValue('role') == 'procurement'){
	myListCollector.setQuery('type=icon function^parents_filters=procurement');
	}
	else if (g_form.getValue('role') == 'superuser procurement'){
	myListCollector.setQuery('type=icon function^parents_filters=superuser procurement');
	}

	//g_form.getValue('filter_operating_unit'));

	var myListCollector2 = g_list.get("please_select_functional_areas_access_should_be_removed");
	myListCollector2.reset();
	if (g_form.getValue('role') == 'grn'){
	myListCollector2.setQuery('type=icon function^parents_filters=grn');
	}
	else if (g_form.getValue('role') == 'finance fbsby'){
	myListCollector2.setQuery('type=icon function^parents_filters=finance fbsby');
	}
	else if (g_form.getValue('role') == 'commercial'){
	myListCollector2.setQuery('type=icon function^parents_filters=commercial');
	}
	else if (g_form.getValue('role') == 'finance fbsmx'){
	myListCollector2.setQuery('type=icon function^parents_filters=finance fbsmx');
	}
	else if (g_form.getValue('role') == 'finance'){
	myListCollector2.setQuery('type=icon function^parents_filters=finance');
	}
	else if (g_form.getValue('role') == 'superuser finance'){
	myListCollector2.setQuery('type=icon function^parents_filters=superuser finance');
	}
	else if (g_form.getValue('role') == 'procurement'){
	myListCollector2.setQuery('type=icon function^parents_filters=procurement');
	}
	else if (g_form.getValue('role') == 'superuser procurement'){
	myListCollector2.setQuery('type=icon function^parents_filters=superuser procurement');
	}

	}else if(newValue == ''){

	//g_form.clearValue('please_select_functional_areas_access_should_be_added');
	//g_form.clearValue('please_select_functional_areas_access_should_be_removed');

	var myListCollector3 = g_list.get("please_select_icon_company_s_that_access_should_be_added");
	myListCollector3.reset();
	myListCollector3.setQuery('type=icon function');

	var myListCollector4 = g_list.get("please_select_icon_company_s_that_access_should_be_removed");
	myListCollector4.reset();
	myListCollector4.setQuery('type=icon function');		

	}


}
