catalog_script_client.script:
function onLoad() {
   //Type appropriate comment here, and begin script below

	var ga = new GlideAjax('x_sksw_uk_itsm.UKRequestUtil');
	ga.addParam('sysparm_name', 'getCartLocation');
	ga.addParam('sysparm_username', g_user.userName);
	ga.getXML(getCartLocation);

	function getCartLocation(response) {
	  var answer = response.responseXML.documentElement.getAttribute("answer");
	  if(answer)
		  {
			  g_form.setMandatory('location', false);
		g_form.setMandatory('location_address',false);	
		g_form.setMandatory('location_city',false);
			g_form.setMandatory('location_county',false);
			g_form.setMandatory('location_post_code',false);
			g_form.setMandatory('requested_for',false);
			g_form.setMandatory('contact_number',false);
			 g_form.setMandatory('additional_information',false); 



		g_form.setReadOnly('location',true);
		g_form.setReadOnly('location_address',true);	
		g_form.setReadOnly('location_city',true);
			g_form.setReadOnly('location_county',true);
			g_form.setReadOnly('location_post_code',true);
			g_form.setReadOnly('requested_for',true);
			g_form.setReadOnly('contact_number',true);
			  g_form.setReadOnly('additional_information',true);


		  }
	}

}
