catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
	if (isLoading || newValue == '') {
		return;
	}

	//Type appropriate comment here, and begin script below
	var gr = new GlideRecord('sys_user');
	gr.addQuery('sys_id',newValue);
	gr.query(fillLocation);
	//alert('newvalue ' + newValue);





	function fillLocation(gr)
	{
		if (gr.next())
			{
			//alert('in function');
			g_form.setValue('location',gr.location);
			}

		var grL = new GlideRecord('cmn_location');
		grL.addQuery('sys_id',gr.location);
		grL.query(fillFields);
		//alert('newvalue ' + newValue);
		function fillFields(grL)
		{
			if (grL.next())
				{
				//alert('in function');
				g_form.setValue('location_address',grL.street);
				g_form.setValue('location_city',grL.city);
					g_form.setValue('location_county',grL.state);
					g_form.setValue('location_post_code',grL.zip);


			}
		}

	}






}
