catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }

   //Type appropriate comment here, and begin script below

	//alert('script');
	//alert(g_form.getValue('device_to_be_replaced'));
	//alert(g_form.getValue('is_the_device_to_be_replaced_correct'));
	if(g_form.getValue('is_the_device_to_be_replaced_correct') == 'Yes')
		{
	var oldDevice = g_form.getValue('device_to_be_replaced');

	var ga = new GlideAjax('EOLgetNewDevice');
	ga.addParam('sysparm_name', 'getNewDevice'); 
	ga.addParam('oldDevice', oldDevice);
	ga.getXML(answer);
		}
	else if(g_form.getValue('is_the_device_to_be_replaced_correct') == 'No')
		{
		g_form.setValue('replacement_device',null);
		g_form.setValue('replacement_device_details',null);
		}


}

function answer(response) {
	var ans = response.responseXML.documentElement.getAttribute("answer");
///	alert(ans);
	g_form.setValue('replacement_device',ans);	
}
