catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
	if (isLoading || newValue == '') {
		return;
	}

	//alert('hi ' + newValue);
	//Type appropriate comment here, and begin script below
	//var loc = g_form.getValue('location');
	var street = new GlideAjax('EOLgetLocation');
	street.addParam('sysparm_name', 'getStreet'); 
	street.addParam('location', newValue);
	street.getXML(ansStreet);	

	var city = new GlideAjax('EOLgetLocation');
	city.addParam('sysparm_name', 'getCity'); 
	city.addParam('location', newValue);
	city.getXML(ansCity);	

	var county = new GlideAjax('EOLgetLocation');
	county.addParam('sysparm_name', 'getCounty'); 
	county.addParam('location', newValue);
	county.getXML(ansCounty);


	var zip = new GlideAjax('EOLgetLocation');
	zip.addParam('sysparm_name', 'getZip'); 
	zip.addParam('location', newValue);
	zip.getXML(ansZip);

}

function ansStreet(response) {
	var ans = response.responseXML.documentElement.getAttribute("answer");
	g_form.setValue('location_address',ans);	
}


function ansCity(response) {	
	var ans = response.responseXML.documentElement.getAttribute("answer");
	g_form.setValue('location_city',ans);	
}


function ansCounty(response) {
	var ans = response.responseXML.documentElement.getAttribute("answer");
	g_form.setValue('location_county',ans);	
}


function ansZip(response) {
	var ans = response.responseXML.documentElement.getAttribute("answer");
	g_form.setValue('location_post_code',ans);	
}
