catalog_ui_policy.script_false:
function onCondition() {

	//g_form.clearValue('please_select_icon_company_s_that_access_should_be_added');
	//g_form.clearValue('please_select_icon_company_s_that_access_should_be_removed');

	var myListCollector = g_list.get("please_select_icon_company_s_that_access_should_be_added");
	myListCollector.reset();
	myListCollector.setQuery('type=icon company');

	var myListCollector2 = g_list.get("please_select_icon_company_s_that_access_should_be_removed");
	myListCollector2.reset();
	myListCollector2.setQuery('type=icon company');

}

