sp_widget.css:
.options-btn {
    border:none;
    background-image:none;
    background-color:transparent;
    -webkit-box-shadow: none;
    -moz-box-shadow: none;
    box-shadow: none;
}

.ticket-fields {
  margin-left: -7px;
  margin-right: -8px;
  -webkit-margin-before: 0;
  -webkit-margin-after: 0;

  dt {
    //font-weight: normal;
    margin-bottom: 10px;
  }
  dd {
    margin-bottom: 10px;
  }
}

sp_widget.script:
(function(){
	data.pickupMsg = gs.getMessage(options.pickup_msg);
	var gr = $sp.getRecord();
	if (gr == null)
		return;

	data.canRead = gr.canRead();
	if (!data.canRead) 
		return;

	var approvers=[];
	var approver = new GlideRecord('sysapproval_approver');
	//approver.addQuery('document_id',gr.sys_id);
	approver.addEncodedQuery('document_id='+gr.sys_id+'^ORsysapproval='+gr.sys_id)
	approver.query();
	var t = 0;
	while(approver.next()){
		t++;
		approvers.push({ukapprover:approver.approver.name+' ('+approver.getDisplayValue('state')+')'});
	}

	var agent = "";
	var a = $sp.getField(gr, 'assigned_to');
	if (a != null)
		agent = a.display_value;

	var fields = $sp.getFields(gr, 'number,state,priority,sys_created_on');
	if (gr.getValue("sys_mod_count") > 0)
		fields.push($sp.getField(gr, 'sys_updated_on'));

	if (gr.getValue('price') > 0)
		fields.push($sp.getField(gr, 'price'));

	if (gr.getValue('recurring_price') > 0) {
		var rp = $sp.getField(gr, 'recurring_price');
		if (gr.isValidField("recurring_price"))
			rp.display_value = rp.display_value + " " + gr.getDisplayValue("recurring_frequency");
		fields.push(rp);
	}
	if (gr.isValidField("quantity"))
		fields.push($sp.getField(gr, 'quantity'));

	var appvis = false;
	if(t>0) appvis=true;

	if((gr.sys_class_name=='sc_req_item' || gr.sys_class_name=='sc_request') && appvis==true)
	fields.push($sp.getField(gr, 'approval'));

	data.approvervisible = appvis;
	data.ukapprovers = approvers;
	data.tableLabel = gr.getLabel();
	data.fields = fields;
	data.variables = $sp.getVariablesArray();
	data.agent = agent;
	data.agentPossible = gr.isValidField("assigned_to");
	data.table = gr.getTableName();
	data.sys_id = gr.getUniqueValue();
})()
