sys_script.script:
(function executeRule(current, previous /*null when async*/) {

   if(current.urgency == 3 && current.impact == 3){
	 current.priority = 4;
   }else if(current.urgency ==  3 && current.impact == 2){
	 current.priority = 4;	   	
   }else if(current.urgency ==  3 && current.impact == 1){
	   current.priority = 4;
   }else if(current.urgency ==  3 && current.impact == 0){
	   current.priority = 4;	   
   }else if(current.urgency ==  2 && current.impact == 3){
	   current.priority = 4;	   
   }else if(current.urgency ==  2 && current.impact == 2){
	   current.priority = 4;	   
   }else if(current.urgency == 2 && current.impact == 1){
	   current.priority = 3;	   
   }else if(current.urgency == 2 && current.impact == 0){
	   current.priority = 3;	   
   }else if(current.urgency == 1 && current.impact == 3){
	   current.priority = 4;	   
   }else if(current.urgency ==  1 && current.impact == 2){
	   current.priority = 3;	   
   }else if(current.urgency ==  1 && current.impact == 1){
	   current.priority = 2;	   
   }else if(current.urgency ==  1 && current.impact == 0){
	   current.priority = 2;	   
   }else if(current.urgency == 0 && current.impact == 3){
	   current.priority = 4;
   }else if(current.urgency == 0 && current.impact == 2){
	   current.priority = 3;
   }else if(current.urgency == 0 && current.impact == 1){
	   current.priority = 2;
   }else if(current.urgency == 0 && current.impact == 0){
	   current.priority = 1;
   }





})(current, previous);
