sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Add your code here
	var taskStatus = current.x_sksw_uk_itsm_state.getDisplayValue();
	var taskStatusChanges;
	var today = new GlideDateTime();
	var stormAudit1 = new GlideRecord('x_sksw_uk_itsm_eol_pc_refresh');
	stormAudit1.addQuery('u_ctask3',current.sys_id);
	stormAudit1.query();
	//gs.addInfoMessage("stormAudit 1 count " + stormAudit1.getRowCount());
	if(stormAudit1.next())
		{
		//gs.addInfoMessage("inside if");
		if (stormAudit1.getValue('u_ctask3_status_changes'))
			{
			//gs.addInfoMessage("inside second if");
			taskStatusChanges = stormAudit1.getValue('u_ctask3_status_changes') + "\n" + taskStatus + " - " + today;

		}
		else
			{
			//gs.addInfoMessage("inside else");
			taskStatusChanges =  taskStatus + " - " + today;
				//gs.addInfoMessage("taskStatusChanges " + taskStatusChanges);
		}

		//gs.addInfoMessage("outside else");
		//gs.addInfoMessage("taskStatusChanges " + taskStatusChanges);
		stormAudit1.setValue('u_ctask3_status_changes',taskStatusChanges);
		stormAudit1.update();
	}

})(current, previous);
