sys_script_client.script:
function onLoad() {

	if(g_form.getValue('state') == -5){
		//First set a variable with your scope
		var myScope = "x_sksw_uk_itsm";

		//Set the field you want to edit
		var field1 = "start_date";
		var field2 = "end_date";


		//then find the variable and store its original scope so we can revert it later

		var fElement1 = g_form.getGlideUIElement(field1);
		var fElement2 = g_form.getGlideUIElement(field2);


		var originalScope1 = fElement1.getScope() + "";
		var originalScope2 = fElement2.getScope() + "";

		//Update the fields scope to your scope
		fElement1.scope = myScope;
		//Then do whatever it is you wanted to do
		g_form.setMandatory(field1, true);
		//Reset the scope for the next script
		fElement1.scope = originalScope1;

		fElement2.scope = myScope;
		g_form.setMandatory(field2, true);
		fElement2.scope = originalScope2;

	}

}
