sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

	//g_form.addInfoMessage('impact='+newValue);
	//g_form.addInfoMessage('urgency='+g_form.getValue('urgency'));

   if(newValue == 3 && g_form.getValue('urgency') == 3) g_form.setValue('x_sksw_uk_itsm_priority',4);
   if(newValue == 3 && g_form.getValue('urgency') == 2) g_form.setValue('x_sksw_uk_itsm_priority',4);	
   if(newValue == 3 && g_form.getValue('urgency') == 1) g_form.setValue('x_sksw_uk_itsm_priority',3);	  
   if(newValue == 2 && g_form.getValue('urgency') == 1) g_form.setValue('x_sksw_uk_itsm_priority',2);
   if(newValue == 2 && g_form.getValue('urgency') == 2) g_form.setValue('x_sksw_uk_itsm_priority',3);	
   if(newValue == 2 && g_form.getValue('urgency') == 3) g_form.setValue('x_sksw_uk_itsm_priority',4);	
   if(newValue == 1 && g_form.getValue('urgency') == 1) g_form.setValue('x_sksw_uk_itsm_priority',1);
   if(newValue == 1 && g_form.getValue('urgency') == 2) g_form.setValue('x_sksw_uk_itsm_priority',2);	
   if(newValue == 1 && g_form.getValue('urgency') == 3) g_form.setValue('x_sksw_uk_itsm_priority',3);		


}
