sys_script_client.script:
function onLoad() {

	g_form.showFieldMsg('rate_model','The rate model is used to derive hourly rates for the associated resource plans and time cards.','info');

	g_form.showFieldMsg('cost','Estimated cost of the project.');
	g_form.showFieldMsg('capex_cost','Capital expenditure (Capex) for the project.');
	g_form.showFieldMsg('opex_cost','Operational expenditure (Opex) for the project.');	
	g_form.showFieldMsg('budget_cost','Budgeted cost for this project. This field is automatically populated from the project budget breakdowns in the cost plan breakdown table.');	
	g_form.showFieldMsg('work_cost','Actual cost of this project.');	
	g_form.showFieldMsg('forecast_cost','Sum of all actuals for past fiscal periods added to the functional cost for future fiscal periods.');
	g_form.showFieldMsg('benefits','Planned benefit for the project.This value is rolled up from the benefit breakdown of the project.');
	g_form.showFieldMsg('value','The Planned returns value is derived from the difference between the Planned benefit and Planned cost values.');	
	g_form.showFieldMsg('roi','The ROI (return on investment) percentage result is calculated based on values in the Planned return and Estimated cost fields.(Planned return/Estimated cost x 100)');
	g_form.showFieldMsg('discount_rate','Project discount rate.The discount rate is the interest rate to determine the present value of future cash flows.');
	g_form.showFieldMsg('npv_value','Present value of future cash based on the given annual interest rate.This value is a measure for comparing money spent today against future expected financial benefits.');	
	g_form.showFieldMsg('irr_value','Annual interest rate required to achieve an NPV of zero.');	
	g_form.showFieldMsg('estimate_to_completion','Estimate to calculation is calculated by aggregating the functional cost on the breakdowns of current and future fiscal periods');	

}
