sys_script_client.script:
function onLoad() {

	if(g_form.getValue('cab_required') == 'false'){

		//The cab_required is true, then change to false. Visible function not working. Because of this, set value then run visible function and clear value.
		g_form.setValue('cab_date','1');
		g_form.setValue('cab_delegate','1');
		g_form.setValue('cab_recommendation','1');

		g_form.setVisible('cab_date',false);
		g_form.setVisible('cab_delegate',false);
		g_form.setVisible('cab_recommendation',false);

		g_form.clearValue('cab_date');
		g_form.clearValue('cab_delegate');
		g_form.clearValue('cab_recommendation');

	}else if(g_form.getValue('cab_required') == 'true'){

		g_form.setVisible('cab_delegate',true);
		g_form.setVisible('cab_recommendation',true);
		g_form.setVisible('cab_date',true);
		g_form.hideFieldMsg('cab_date',true);
		g_form.showFieldMsg('cab_date','UK IT Change Advisory Boards are held 09:30 Tuesday\'s and Friday\'s.');

		//First set a variable with your scope
		var myScope = "x_sksw_uk_itsm";

		//Set the field you want to edit
		var field1 = "cab_date";
		var field2 = "cab_delegate";
		var field3 = "cab_recommendation";


		//then find the variable and store its original scope so we can revert it later

		var fElement1 = g_form.getGlideUIElement(field1);
		var fElement2 = g_form.getGlideUIElement(field2);
		var fElement3 = g_form.getGlideUIElement(field3);


		var originalScope1 = fElement1.getScope() + "";
		var originalScope2 = fElement2.getScope() + "";
		var originalScope3 = fElement3.getScope() + "";


		//Update the fields scope to your scope
		fElement1.scope = myScope;
		//Then do whatever it is you wanted to do
		g_form.setMandatory(field1, true);
		//Reset the scope for the next script
		fElement1.scope = originalScope1;

		fElement2.scope = myScope;
		g_form.setMandatory(field2, true);
		fElement2.scope = originalScope2;

		fElement3.scope = myScope;
		g_form.setMandatory(field3, true);
		fElement3.scope = originalScope3;

	}
}
