function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

	if(g_form.getValue('x_sksw_uk_itsm_assignment_group') != 'f2f122711bfc8c50445f3150cd4bcb17'){
			var myScope3 = "x_sksw_uk_itsm";
			var field3 = "x_sksw_uk_itsm_license_available";
			var fElement3 = g_form.getGlideUIElement(field3);
			var originalScope3 = fElement3.getScope() + "";

				fElement3.scope = myScope3;
				g_form.setMandatory(field3, false);
				g_form.setVisible(field3, false);
				fElement3.scope = originalScope3;
	}

	else if(g_form.getValue('x_sksw_uk_itsm_state') == '-5'){
			var myScope = "x_sksw_uk_itsm";
			var field = "x_sksw_uk_itsm_license_available";
			var fElement = g_form.getGlideUIElement(field);
			var originalScope = fElement.getScope() + "";

				fElement.scope = myScope;
				g_form.setMandatory(field, false);
				g_form.setVisible(field, true);
				fElement.scope = originalScope;


	}

	else if((g_form.getValue('x_sksw_uk_itsm_assignment_group') == 'f2f122711bfc8c50445f3150cd4bcb17') && g_form.getValue('x_sksw_uk_itsm_state') != '-5'){
			var myScope2 = "x_sksw_uk_itsm";
			var field2 = "x_sksw_uk_itsm_license_available";
			var fElement2 = g_form.getGlideUIElement(field2);
			var originalScope2 = fElement2.getScope() + "";

				fElement2.scope = myScope2;
				g_form.setMandatory(field2, true);
				g_form.setVisible(field2, true);
				fElement2.scope = originalScope2;


	}

}
