sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

	g_form.setVisible('x_sksw_uk_itsm_storm_status',false);
	g_form.setVisible('x_sksw_uk_itsm_sublocation',false);
	g_form.setVisible('x_sksw_uk_itsm_imei',false);
	g_form.setVisible('x_sksw_uk_itsm_imei2',false);
	g_form.setVisible('x_sksw_uk_itsm_carrier_imei',false);
	g_form.setVisible('x_sksw_uk_itsm_vsdm_last_import_date',false);	
	g_form.setVisible('x_sksw_uk_itsm_mobile_contract_vendor',false);
	g_form.setVisible('x_sksw_uk_itsm_colour',false);
	g_form.setVisible('x_sksw_uk_itsm_memory',false);	
	g_form.setVisible('x_sksw_uk_itsm_dc_th_name',false);
	g_form.setVisible('x_sksw_uk_itsm_rack',false);
	g_form.setVisible('x_sksw_uk_itsm_position',false);		
	g_form.setVisible('x_sksw_uk_itsm_project_name',false);	
	g_form.setVisible('x_sksw_uk_itsm_u_space',false);	
	g_form.setVisible('x_sksw_uk_itsm_business',true);	


	g_form.getReference('model_category',name);

	function name(category){
		var catname = category.name;

		if(catname == 'Computer'){
			g_form.setVisible('x_sksw_uk_itsm_storm_status',true);
			g_form.setVisible('x_sksw_uk_itsm_sublocation',true);
			g_form.setVisible('x_sksw_uk_itsm_business',false);	
		}

		if(catname == 'Communication Device'){
			g_form.setVisible('x_sksw_uk_itsm_sublocation',true);
			g_form.setVisible('x_sksw_uk_itsm_imei',true);
			g_form.setVisible('x_sksw_uk_itsm_imei2',true);
			g_form.setVisible('x_sksw_uk_itsm_carrier_imei',true);
			g_form.setVisible('x_sksw_uk_itsm_vsdm_last_import_date',true);			
			g_form.setVisible('x_sksw_uk_itsm_mobile_contract_vendor',true);
			g_form.setVisible('x_sksw_uk_itsm_colour',true);	
			g_form.setVisible('x_sksw_uk_itsm_memory',true);			

		}

		if(catname.includes('Server') || catname == 'Printer'){
			g_form.setVisible('x_sksw_uk_itsm_dc_th_name',true);
			g_form.setVisible('x_sksw_uk_itsm_rack',true);
			g_form.setVisible('x_sksw_uk_itsm_position',true);		
			g_form.setVisible('x_sksw_uk_itsm_project_name',true);			
			g_form.setVisible('x_sksw_uk_itsm_u_space',true);			
		}



	}


//	g_form.addInfoMessage('category='+);



}
