sys_script_client.script:
function onLoad() {

   if(g_form.getValue('urgency') == 3 && g_form.getValue('impact') == 3) g_form.setValue('x_sksw_uk_itsm_priority',4);
   if(g_form.getValue('urgency') == 3 && g_form.getValue('impact') == 2) g_form.setValue('x_sksw_uk_itsm_priority',4);	
   if(g_form.getValue('urgency') == 3 && g_form.getValue('impact') == 1) g_form.setValue('x_sksw_uk_itsm_priority',3);	  
   if(g_form.getValue('urgency') == 2 && g_form.getValue('impact') == 1) g_form.setValue('x_sksw_uk_itsm_priority',2);
   if(g_form.getValue('urgency') == 2 && g_form.getValue('impact') == 2) g_form.setValue('x_sksw_uk_itsm_priority',3);	
   if(g_form.getValue('urgency') == 2 && g_form.getValue('impact') == 3) g_form.setValue('x_sksw_uk_itsm_priority',4);	
   if(g_form.getValue('urgency') == 1 && g_form.getValue('impact') == 1) g_form.setValue('x_sksw_uk_itsm_priority',1);
   if(g_form.getValue('urgency') == 1 && g_form.getValue('impact') == 2) g_form.setValue('x_sksw_uk_itsm_priority',2);	
   if(g_form.getValue('urgency') == 1 && g_form.getValue('impact') == 3) g_form.setValue('x_sksw_uk_itsm_priority',3);	

}
