function onSubmit() {
	if(g_form.getValue('x_sksw_uk_itsm_state') == '4'){
	var ajax = new GlideAjax ('UKProblemUtil');
	ajax.addParam('sysparm_name','getPrbTasks');
	ajax.addParam('sysparm_problem_id', g_form.getUniqueValue());
	ajax.getXML(getPrbTasks);
	}
	function getPrbTasks(response){
		var answer = response.responseXML.documentElement.getAttribute("answer");
		if(answer == false){
			g_form.setValue('x_sksw_uk_itsm_state', '1');
			g_form.addInfoMessage(answer);
			g_form.save();
		}
		return answer;
	}

