	if (newValue == '462bf5921b3c0090445f3150cd4bcbb3')  //Not in CMDB')
		{
		//g_form.addInfoMessage('First condition true');
		g_form.setReadOnly('x_sksw_uk_itsm_u_missing_ci', false);
