sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   //Type appropriate comment here, and begin script below
	if(g_form.getValue('x_sksw_uk_itsm_u_sr_type') == 'Procurement')
		{
			//g_form.setDisplay('x_sksw_uk_itsm_u_sr_subttype',true);
			g_form.setMandatory('x_sksw_uk_itsm_u_sr_subttype',true);			
		}
	else if(g_form.getValue('x_sksw_uk_itsm_u_sr_type') == 'Device Leasing')
		{
			//g_form.setDisplay('x_sksw_uk_itsm_u_sr_subttype',true);
			g_form.setMandatory('x_sksw_uk_itsm_u_sr_subttype',true);			
		}
	else if(g_form.getValue('x_sksw_uk_itsm_u_sr_type') == 'Asset Mgmt')
		{
			//g_form.setDisplay('x_sksw_uk_itsm_u_sr_subttype',true);
			g_form.setMandatory('x_sksw_uk_itsm_u_sr_subttype',true);			
		}
	else if(g_form.getValue('x_sksw_uk_itsm_u_sr_type') == 'Config Mgmt')
		{
			//g_form.setDisplay('x_sksw_uk_itsm_u_sr_subttype',true);
			g_form.setMandatory('x_sksw_uk_itsm_u_sr_subttype',true);			
		}
	else 
		{
			//g_form.setDisplay('x_sksw_uk_itsm_u_sr_subttype',true);
			g_form.setMandatory('x_sksw_uk_itsm_u_sr_subttype',false);			
		}

}
