sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {


template.print("<p style='margin: 0cm; margin-bottom: .0001pt; line-height: 120%; font-size: 13px; font-family: 'Arial',sans-serif;'><span style='font-size: 15px; line-height: 120%;'>Requested items:</span></p>\n");

var gr = new GlideRecord("sc_req_item");
gr.addQuery("request", current.getUniqueValue());
gr.query();
while(gr.next()) {

var stage = gr.stage.getDisplayValue();
//if (JSUtil.nil(stage))
//stage = gr.stage.getChoiceValue();
template.print("\n" + gr.number + ": " + gr.cat_item.getDisplayValue() + ", Stage: " + stage + "\n");
}


})(current, template, email, email_action, event);
