sys_script_include.script:
var EOLgetLocation = Class.create();
EOLgetLocation.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getStreet : function()
	{

		var loc = this.getParameter('location');
		var street = new GlideRecord('cmn_location');
		street.addQuery('sys_id',loc);
		street.query();
		if (street.next())
			{
			return street.getValue('street');
		}
	},


	getCity : function()
	{

		var loc = this.getParameter('location');
		var city = new GlideRecord('cmn_location');
		city.addQuery('sys_id',loc);
		city.query();
		if (city.next())
			{
			return city.getValue('city');
		}
	},


	getCounty : function()
	{

		var loc = this.getParameter('location');
		var county = new GlideRecord('cmn_location');
		county.addQuery('sys_id',loc);
		county.query();
		if (county.next())
			{
			return county.getValue('state');
		}
	},



	getZip : function()
	{

		var loc = this.getParameter('location');
		var zip = new GlideRecord('cmn_location');
		zip.addQuery('sys_id',loc);
		zip.query();
		if (zip.next())
			{
			return zip.getValue('zip');
		}
	},

	type: 'EOLgetLocation'

});
