sys_script_include.script:
var getContactNumber = Class.create();
getContactNumber.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {


	getNumber : function(){
		var user = this.getParameter('userID');	
		var cNumber;
		var gr = new GlideRecord('sys_user');
		gr.addQuery('sys_id',user);
		gr.query();		
		if (gr.next())
			{
			//alert('in if');
			if(gr.getValue('phone'))
				{
					cNumber = gr.phone;
				}
			else if	(gr.getValue('mobile_phone'))
				{
					cNumber = gr.mobile_phone;
				}
			return cNumber;

		}
	},


    type: 'getContactNumber'
});
