var EOLgetDevice = Class.create();
EOLgetDevice.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getDevice : function(userid){
		var gr = new GlideRecord('x_sksw_uk_itsm_eol_pc_refresh');
		gr.addQuery('u_user_id',userid);
		gr.addQuery('u_req',null);
		gr.addQuery('u_ritm',null);
		gr.addNotNullQuery('u_invitation_email_sent_date');
		gr.query();		
		if (gr.next())
			{
			//alert('in if');
			return gr.getValue('device');

		}
	},

	type: 'EOLgetDevice'
});
