sys_script_include.script:
var UKRequestUtil = Class.create();
UKRequestUtil.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getUserNum:function(){

		var gruser = new GlideRecord('sys_user'); 
		gruser.get(this.getParameter('sysparm_user_id'));
		//if(grprb.next()){
			//gs.addInfoMessage('found record');
			if(gruser) { 
					return gruser.phone;
			}
		//}
	},

	getCartLocation:function(){
		var sccartitem = new GlideRecord("sc_cart_item");
		sccartitem.addQuery("sys_created_by",this.getParameter('sysparm_username'));
		sccartitem.addQuery("active",true);
		sccartitem.query();	
		if(sccartitem.next()){
			var sccartitemopt = new GlideRecord("sc_item_option");
			sccartitemopt.addEncodedQuery("cart_item="+sccartitem.sys_id+"^item_option_new.name=location");
			sccartitemopt.query();	
			if(sccartitemopt.next()){
			return sccartitemopt.value;
			}
		}
	},


	getCartLocAddress:function(){
		var sccartitem = new GlideRecord("sc_cart_item");
