var UKProblemUtil = Class.create();
UKProblemUtil.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getPrbTasks :function(){

		var grprb = new GlideRecord('problem_task'); 
		grprb.addActiveQuery(); 
		grprb.addQuery('problem', this.getParameter('sysparm_problem_id'));
		grprb.query();

		//if(grprb.next()){
			//gs.addInfoMessage('found record');
			while(grprb.next()) { 
					gs.addErrorMessage('Problems may not be closed while problem tasks are still open');
					return false; }
		//}
	},

    type: 'UKProblemUtil'
});
