sys_script_include.script:
var EOLgetNewDevice = Class.create();
EOLgetNewDevice.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getNewDevice : function(){


		var par = this.getParameter('oldDevice');	
		var oldModel;
		var grC = new GlideRecord('cmdb_ci_computer');
		grC.addQuery('sys_id', par);
		grC.query();			
		if (grC.next())
			{
			oldModel= grC.getValue('model_id');
			var gr = new GlideRecord('x_sksw_uk_itsm_pc_model_mapping');
			gr.addQuery('from_model',oldModel);
			gr.query();			
			if (gr.next())
				{
				//alert('in if');
				return	gr.getValue('u_to_model');				
			}
		}

	},

	getNewDeviceDesc : function(){
		var par = this.getParameter('newDevice');
		var grDesc = new GlideRecord('sc_cat_item');
		grDesc.addQuery('sys_id', par);	
		grDesc.query();	
		if (grDesc.next())
			{
		return grDesc.description.toString();
			}



	},

	type: 'EOLgetNewDevice'
});
