sys_transform_script.script:
(function runTransformScript(source, map, log, target /*undefined onStart*/ ) {

	var cmdb = new GlideRecord('cmdb_ci');
	cmdb.addEncodedQuery('serial_number='+source.u_serial+'^x_sksw_uk_itsm_scope=UK ITSM');
	cmdb.query();
	if(cmdb.getRowCount()>0){
		if (action == "insert") {
			ignore = true;
		}
	}else{

		var comm = new GlideRecord('cmdb_ci_comm');
		comm.addQuery('sys_id',target.ci);
		comm.query();
		if(comm.next()){
			comm.name = source.u_hostname;
			comm.x_sksw_uk_itsm_vsdm_last_import_date = source.u_vsdm_last_import_date;
			comm.x_sksw_uk_itsm_operating_system = source.u_operating_system;
			comm.x_sksw_uk_itsm_scope = "Skanska UK";
			comm.operational_status = source.u_operational_status;			
			comm.update();
		}}


	})(source, map, log, target);
