sys_transform_script.script:
(function runTransformScript(source, map, log, target /*undefined onStart*/ ) {


	var cmdb = new GlideRecord('cmdb_ci');
	cmdb.addEncodedQuery('serial_number='+source.u_serial+'^x_sksw_uk_itsm_scope=UK ITSM');
	cmdb.query();
	if(cmdb.getRowCount()>0){
		if (action == "insert") {
			ignore = true;
		}
	}else{

	var printer = new GlideRecord('cmdb_ci_printer');
	printer.addQuery('sys_id',target.ci);
	printer.query();
	if(printer.next()){
		//ci.serial_number = source.u_serial;
		printer.name = source.u_hostname;
		printer.ip_address = source.u_ip_address;
		printer.mac_address = source.u_mac_address;
		printer.operational_status = source.u_operational_status;
		printer.x_sksw_uk_itsm_scope = "UK ITSM";
		printer.update();
	}}

})(source, map, log, target);
