sys_ui_action.script:
function launchDialogToFundProject(){
	checkFiscalYearAvailable();
}

function checkFiscalYearAvailable() {
	var fiscalYearAvailable = false;
	var ga = new GlideAjax('global.PPMFiscalPeriodAjax');
	ga.addParam('sysparm_name', 'isFiscalYearAvailable');
	ga.getXML(checkFiscalYearAvailableCallback);
}

function checkFiscalYearAvailableCallback(response) {
	var fiscalYearAvailable = response.responseXML.documentElement.getAttribute("answer");

	if(fiscalYearAvailable!='true') {
		g_form.clearMessages(); 
		g_form.addInfoMessage(getMessage("Generate fiscal calendar to set project budget"));
		return false;
	}

	launchDialogToFundProjectAsync();
}

function launchDialogToFundProjectAsync(){
	var sysId = typeof rowSysId == 'undefined' || rowSysId == null ? gel('sys_uniqueValue').value : rowSysId;
	//window.GlideModal ? GlideModal : 
	var dialogClass =  GlideDialogWindow;
	var dialog = new dialogClass('entity_funding');
	dialog.setTitle(getMessage('Project Budget'));
	dialog.setPreference('sysparm_entity_id', sysId);
	dialog.setPreference('sysparm_entity_type', 'task');
	dialog.setWidth(400);
	dialog.render();
}
