var approver = new GlideRecord('sysapproval_approver');
approver.addEncodedQuery("sysapproval="+current.sys_id+"^state=requested");
approver.query();
if(approver.hasNext()){
current.variables.genericrequest_send_approval = "True";
current.variables.genericrequest_process_decision = "proceed";
}else{
	gs.addErrorMessage('Please add approvers under Approvers Tab.');	
}
current.approval = 'requested';
current.update();
action.setRedirectURL(current);
