sysauto_script.condition:
//This function runs in March, June, September & December

function checkquarterly() {


var now = new Date();


var month = now.getMonth();


var result = false;


if(month == 3 || month == 6 || month == 9 || month == 12) {


result = true;


}


return result;


}


checkquarterly();

