sysauto_script.condition:
//This function doesn't run on Sunday, Saturday.

function checkWeekdays() {


var now = new Date();


var day = now.getDay();


var result = false;


if(day != 0 && day != 6) {


result = true;


}


return result;


}


checkWeekdays();

