sysauto_script.script:
	var userMember = new GlideRecord('sys_user_grmember');
//Find users in UK Project Management Office, UK Solutions & Planning, and UK Project Delivery
//Also make sure user is in 'Skanska UK' scope
userMember.addEncodedQuery('group=0c746eb11bfc8c50445f3150cd4bcb2b^ORgroup=47e36ab11bfc8c50445f3150cd4bcbe9^ORgroup=521422f11bfc8c50445f3150cd4bcbd5^user.x_sksw_uk_itsm_scope=Skanska UK');
	userMember.query();
	while(userMember.next()){
		//gs.addInfoMessage('user='+user.user_id);
		//var grUser = userMember.user;
		var grUser = new GlideRecord('sys_user');
		grUser.addQuery('sys_id', userMember.user);
		grUser.query();
		while(grUser.next()){
			grUser.country = 'US';
			grUser.update();
		}
	}
//all other users will be set to United Kingdom
		var grUser2 = new GlideRecord('sys_user');
		grUser2.addEncodedQuery('x_sksw_uk_itsm_scope=Skanska UK^country=NULL');
		grUser2.query();
		while(grUser2.next()){
			grUser2.country = 'GB';
			grUser2.update();
		}
