sysauto_script.script:
var cart = new Cart();

// Add in cart, sys_id for Internal Service Desk Request catalog item

var item = cart.addItem('3D729897791b3d4c104d21ed3fad4bcbcb');

// Set requested for, sys_id for requestor

cart.setVariable(item, 'requested_for', '3Df00ecc8bdbcad700b44772b10f9619a4');

//Set date needed to current date

var gdt = new GlideDateTime();

cart.setVariable(item,'date_needed',gdt);

//Set business service to conference room

cart.setVariable(item, 'business_service', '3Dec97bc8e1b050c10445f3150cd4bcb1e');


//Set short description

cart.setVariable(item, 'short_description', 'itrent check');

//Set notes

cart.setVariable(item, 'notes', 'do itrent check');

// Places order and creates request

cart.placeOrder();
