sysauto_script.condition:
//This function runs this script on February, May, August & November.

function checkquarterly() {


var now = new Date();


var month = now.getMonth();


var result = false;


if(month == 2 || month == 5 || month == 8 || month == 11) {


result = true;


}


return result;


}


checkquarterly();

