sysauto_script.script:
runCheck();
function runCheck()
{
	var remDate;
	var today =  new GlideDateTime();
	var grEOLFind = new GlideRecord('x_sksw_uk_itsm_eol_pc_refresh');
	grEOLFind.addNullQuery('u_req');
	grEOLFind.addNullQuery('u_ritm');
	grEOLFind.query();
	gs.addInfoMessage(grEOLFind.getRowCount());
	while (grEOLFind.next())
		{
		if(grEOLFind.getValue('u_reminder_emails_sent_dates'))
			{
			 remDate = grEOLFind.getValue('u_reminder_emails_sent_dates');
			remDate = remDate + '\n' + today;
		}
		else
			{
				remDate = today;
			}			
		grEOLFind.setValue('u_reminder_emails_sent_dates',remDate);
		grEOLFind.update();
	}
}
