sysauto_script.condition:
//This function runs the script from Monday to Friday.

function checkWeekdays() {


var now = new Date();


var day = now.getDay();


var result = false;


if(day != 0 && day != 6) {


result = true;


}


return result;


}


checkWeekdays();

