sysauto_script.condition:
//This function only runs from Tuesday to Saturday

function checkWeekdays() {


var now = new Date();


var day = now.getDay();


var result = false;

//if statement below 0 = sunday 1= monday 
	//This reads as if day is not sunday or monday run this scheduled job

if(day != 0 && day != 1) {


result = true;


}


return result;


}


checkWeekdays();

