var gr = new GlideRecord('sc_task');
gr.addQuery('sys_id',workflow.scratchpad.task1SysId);
gr.query();
if(gr.next())
	{
		if (gr.getValue('active') == false)
			{
				answer = true;
				workflow.scratchpad.newDevice = gr.getValue('x_sksw_uk_itsm_cmdb_ci');
			}
		else
			{
				answer = false;
			}
		
	}

