var gdt = new GlideDateTime();
var grNum = new GlideRecord('sc_task');
grNum.addQuery('request_item',current.sys_id);
grNum.query();
if(grNum.next()){
workflow.scratchpad.task3Number = grNum.number;
workflow.scratchpad.task3SysId = grNum.sys_id;	
workflow.scratchpad.task3Created = grNum.opened_at;	
workflow.scratchpad.task3Title = grNum.short_description;		
}


var grEOL = new GlideRecord('x_sksw_uk_itsm_eol_pc_refresh');
grEOL.addQuery('u_user_id',current.opened_by);
grEOL.addQuery('device',current.variables.device_to_be_replaced);
grEOL.addNullQuery('u_req');
grEOL.addNullQuery('u_ritm');
grEOL.query();
if (grEOL.next())
	{
		//grEOL.u_req = current.request;
		
		grEOL.setValue('u_req',current.request);
		grEOL.setValue('u_req_create_date',current.request.opened_at);
		grEOL.setValue('u_ritm',current.sys_id);
		grEOL.setValue('u_ritm_create_date',current.opened_at);
		grEOL.setValue('u_ctask3',workflow.scratchpad.task3SysId );
		grEOL.setValue('u_ctask3_create_date',workflow.scratchpad.task3Created);
		grEOL.setValue('u_ctask3_title',workflow.scratchpad.task3Title);
		//grEOL.setValue('u_ctask3_email_to_storm',gdt);
		grEOL.update();		
	}




var grPrice = new GlideRecord('sc_cat_item');
grPrice.addQuery('sys_id',current.variables.replacement_device);
grPrice.query();
if (grPrice.next())
	{
	workflow.scratchpad.itemPrice  = grPrice.getValue('price');	
		workflow.scratchpad.itemID = grPrice.getValue('u_x_sksw_fi_atea_int_item_id');    //current.cat_item.u_x_sksw_fi_atea_int_item_id;
workflow.scratchpad.itemManufacturer  = grPrice.getDisplayValue('vendor'); 
	//current.cat_item.vendor.name;
	}


