sys_variable_value.value:
workflow.scratchpad.return = [];
workflow.scratchpad.return  = current.variables.device_to_be_replaced; // Variable name in your catalog item
workflow.scratchpad.return = workflow.scratchpad.return.toString().split(',');

workflow.scratchpad.DeviceSerialNo = [];
workflow.scratchpad.DeviceAssetNo = [];
workflow.scratchpad.OrderNo = [];

//fetch device details for each device
for(var j=0; j<workflow.scratchpad.return.length;j++){
var grPC = new GlideRecord('cmdb_ci_computer');
grPC.addQuery('sys_id',workflow.scratchpad.return[j]);
grPC.query();
if(grPC.next())
	{
		var serial = grPC.getValue('serial_number');
		workflow.scratchpad.DeviceSerialNo.push(serial);
		var asset = grPC.getValue('asset_tag');
		workflow.scratchpad.DeviceAssetNo.push(asset);
		var gr = new GlideRecord('alm_hardware');
		gr.addQuery('ci',workflow.scratchpad.return[j]);
		gr.query();
		if(gr.next())
			{
				var order  = gr.getValue('x_sksw_uk_itsm_order_number');
				workflow.scratchpad.OrderNo.push(order);
			}
	}
}


for(var i=0; i<workflow.scratchpad.return.length;i++){
var gr = new GlideRecord("sc_task");
gr.initialize();
gr.parent = current.sys_id;
gr.sys_created_by = gs.getUserID();
gr.state = -5; //Pending
gr.assignment_group = '894e9a6c1b51c450445f3150cd4bcbc6';  // Asset LifeCycle
gr.x_sksw_uk_itsm_state = '-5'; //Pending
gr.x_sksw_uk_itsm_assignment_group = '894e9a6c1b51c450445f3150cd4bcbc6'; // Asset LifeCycle	
gr.short_description="BAU - Return a Computer : " + workflow.scratchpad.DeviceAssetNo[i] +  '-' + workflow.scratchpad.DeviceSerialNo[i];
gr.request=current.request;
gr.request_item = current.sys_id;
if(workflow.scratchpad.return.length > 0)
{	
gr.cmdb_ci = workflow.scratchpad.return[i];
gr.x_sksw_uk_itsm_cmdb_ci = workflow.scratchpad.return[i];
}
else
	{
gr.cmdb_ci =  current.variables.device_to_be_replaced;
gr.x_sksw_uk_itsm_cmdb_ci =  current.variables.device_to_be_replaced;
}	
gr.insert();
}

