workflow.scratchpad.task2Number = [];
workflow.scratchpad.task2SysId = [];	
workflow.scratchpad.task2Created = [];	
workflow.scratchpad.task2Title = [];		


var gdt = new GlideDateTime();
var grNum = new GlideRecord('sc_task');
grNum.addQuery('request_item',current.sys_id);
grNum.orderBy('number');
grNum.query();
while (grNum.next()){
var num = grNum.getValue('number');
workflow.scratchpad.task2Number.push(num);
var id = grNum.getValue('sys_id');
workflow.scratchpad.task2SysId.push(id);	
var opened = grNum.getValue('opened_at');
workflow.scratchpad.task2Created.push(opened);
	var desc  = grNum.getValue('short_description');
workflow.scratchpad.task2Title.push(desc);		
}


// insert record in Storm Audit table
for(var i=0; i<workflow.scratchpad.task2Number.length; i++)
	{
var grEOL = new GlideRecord('x_sksw_uk_itsm_eol_pc_refresh');
		grEOL.initialize();
		grEOL.setValue('u_user_id',current.opened_by);
		grEOL.setValue('u_req',current.request);
		grEOL.setValue('device',workflow.scratchpad.return[i]);
		grEOL.setValue('u_req_create_date',current.request.opened_at);
		grEOL.setValue('u_ritm',current.sys_id);
		grEOL.setValue('u_ritm_create_date',current.opened_at);
		grEOL.setValue('u_ctask2',workflow.scratchpad.task2SysId[i]);
		grEOL.setValue('u_ctask2_create_date',workflow.scratchpad.task2Created[i]);
		grEOL.setValue('u_ctask2_title',workflow.scratchpad.task2Title[i]);
		grEOL.setValue('u_ctask2_email_to_storm',gdt);
		grEOL.insert();
	}


workflow.scratchpad.notification = 0;
