// Set the variable 'answer' to true or false to indicate if the condition has been met or not.
var openFlag;
var closeFlag;
var gr = new GlideRecord('sc_task');
gr.addQuery('request_item',current.sys_id);
gr.query();
while(gr.next())
	{
	if(gr.getValue('active') == true)
		{
		openFlag = true;
	}
	else
		{
		closeFlag = true;
	}
}

if(openFlag)
	{
	answer = false;
}
else if(closeFlag)
	{
	answer = true;
}

