var gdt = new GlideDateTime();
var grNum = new GlideRecord('sc_task');
grNum.addQuery('request_item',current.sys_id);
grNum.query();
if(grNum.next()){
workflow.scratchpad.task2Number = grNum.number;
workflow.scratchpad.task2SysId = grNum.sys_id;	
workflow.scratchpad.task2Created = grNum.opened_at;	
workflow.scratchpad.task2Title = grNum.short_description;		
}

gs.info('EOL Return Task Number : ' + workflow.scratchpad.task2Number);

var grEOL = new GlideRecord('x_sksw_uk_itsm_eol_pc_refresh');
grEOL.addQuery('u_user_id',current.opened_by);
grEOL.addQuery('device',current.variables.device_to_be_replaced);
grEOL.addNullQuery('u_req');
grEOL.addNullQuery('u_ritm');
grEOL.query();
if (grEOL.next())
	{
		//grEOL.u_req = current.request;
		
		grEOL.setValue('u_req',current.request);		
		grEOL.setValue('u_req_create_date',current.request.opened_at);
		grEOL.setValue('u_ritm',current.sys_id);
		grEOL.setValue('u_ritm_create_date',current.opened_at);
		grEOL.setValue('u_ctask2',workflow.scratchpad.task2SysId );
		grEOL.setValue('u_ctask2_create_date',workflow.scratchpad.task2Created);
		grEOL.setValue('u_ctask2_title',workflow.scratchpad.task2Title);
		grEOL.setValue('u_ctask2_email_to_storm',gdt);
		grEOL.update();		
	}




var grPrice = new GlideRecord('sc_cat_item');
grPrice.addQuery('sys_id',current.variables.replacement_device);
grPrice.query();
if (grPrice.next())
	{
	workflow.scratchpad.itemPrice  = grPrice.getValue('price');	
		workflow.scratchpad.itemID = grPrice.getValue('u_x_sksw_fi_atea_int_item_id');    //current.cat_item.u_x_sksw_fi_atea_int_item_id;
workflow.scratchpad.itemManufacturer  = grPrice.getDisplayValue('vendor'); 
	//current.cat_item.vendor.name;
	}


var grPC = new GlideRecord('cmdb_ci_computer');
grPC.addQuery('sys_id',current.variables.device_to_be_replaced);
grPC.query();
if(grPC.next())
	{
		
		workflow.scratchpad.oldDeviceSerialNo = grPC.getValue('serial_number');
		workflow.scratchpad.oldDeviceAssetNo = grPC.getValue('asset_tag');
		var gr = new GlideRecord('alm_hardware');
		gr.addQuery('ci',current.variables.device_to_be_replaced);
		gr.query();
		if(gr.next())
			{
				var order  = gr.getValue('x_sksw_uk_itsm_order_number');
				workflow.scratchpad.OrderNo = order;
			}
	}

var gdt = new GlideDateTime();
var grNum = new GlideRecord('sc_task');
grNum.addQuery('request_item',current.sys_id);
grNum.query();
if(grNum.next()){
workflow.scratchpad.task2Number = grNum.number;
workflow.scratchpad.task2SysId = grNum.sys_id;	
workflow.scratchpad.task2Created = grNum.opened_at;	
workflow.scratchpad.task2Title = grNum.short_description;		
