		grEOL.setValue('u_ctask2_create_date',workflow.scratchpad.task2Created);
		grEOL.setValue('u_ctask2_title',workflow.scratchpad.task2Title);
		grEOL.setValue('u_ctask2_email_to_storm',gdt);
		grEOL.update();		
	}




var grPrice = new GlideRecord('sc_cat_item');
grPrice.addQuery('sys_id',current.variables.replacement_device);
grPrice.query();
if (grPrice.next())
	{
	workflow.scratchpad.itemPrice  = grPrice.getValue('price');	
		workflow.scratchpad.itemID = grPrice.getValue('u_x_sksw_fi_atea_int_item_id');    //current.cat_item.u_x_sksw_fi_atea_int_item_id;
workflow.scratchpad.itemManufacturer  = grPrice.getDisplayValue('vendor'); 
