// Set the variable 'answer' to true or false to indicate if the condition has been met or not.
var gr = new GlideRecord('sc_task');
gr.addQuery('sys_id',workflow.scratchpad.task2SysId);
gr.query();
if(gr.next())
	{
		if (gr.getValue('active') == false)
			{
				answer = true;
			}
		else
			{
				answer = false;
			}
		
	}

