var companysc;
for(var i = 0; i < workflow.scratchpad.company.length; i++){
	if(!companysc)
		{
			companysc = workflow.scratchpad.company[i];
		}
	else companysc = companysc + workflow.scratchpad.company[i];
	
}
task.description = companysc;

