var companysc;
for(var i = 0; i < workflow.scratchpad.company.length; i++){
	if(!companysc)
		{
			companysc = workflow.scratchpad.company[i];
		}
	else companysc = companysc + workflow.scratchpad.company[i];
	
}
task.description = companysc;

var company  = current.variables.please_select_icon_company_s_that_access_should_be_added + '';
var OU = current.variables.which_ou_does_this_person_work_in;
var noo = current.variables.access_request_type;
var iport = current.variables.iport_access_check;
workflow.scratchpad.answer = [];
workflow.scratchpad.company = [];

var companyArraySplit = [];

//gs.info('jesseC ' + company );

//gs.info('Riz add info ' + current.variables.any_additional_information);

var ouArray = [];


companyArraySplit = company.split(",");

//fetch Operating unit sys id
//var grOu = new GlideRecord('x_sksw_uk_itsm_icon_settings');
//gs.info('jesse companyArraySplit length' + companyArraySplit.length);
if(!company){
	ouArray.push(OU.toString());
	//gs.info('jesse OU question 3 ' + OU);
}
else{
	for (var j=0 ; j < companyArraySplit.length ; j++) {
	//gs.info('jesse companyArraySplit array' + companyArraySplit[j]);
		var grOu = new GlideRecord('x_sksw_uk_itsm_icon_settings');
		grOu.addQuery('sys_id',  companyArraySplit[j].toString());
		grOu.query();
		//gs.info('jesse count of grOu' + grOu.getRowCount());
		if (grOu.next())
			{
				workflow.scratchpad.company.push(grOu.getValue('security_groups'));
				if(grOu.getValue('operating_unit').toString() == 'eab9e25b1be140104252c8017e4bcbf5'){
					ouArray = [];
					ouArray.push(grOu.getValue('operating_unit').toString());
						j = companyArraySplit.length;
					}
				else ouArray.push(grOu.getValue('operating_unit').toString());
				
			}
	}
}

var grNew = new GlideRecord('x_sksw_uk_itsm_icon_settings');
		grNew.addQuery('sys_id',  '1844165d1bfd88104252c8017e4bcb5c');
		grNew.query();
//gs.info('jesse access type' + current.variables.access_request_type);
if (noo == 'New'){
	//gs.info('jesse in here now');
	if(grNew.next()){
	//gs.info('jesse1 ' + workflow.scratchpad.company);
	workflow.scratchpad.company.push(grNew.getValue('security_groups'));
	//gs.info('jesse2 ' + workflow.scratchpad.company);
	}
}

var grPort = new GlideRecord('x_sksw_uk_itsm_icon_settings');
		grPort.addQuery('sys_id',  '3ac9a04b1b2d84904252c8017e4bcb24');
		grPort.query();
//gs.info('jesse access type' + current.variables.access_request_type);
if (iport){
	//gs.info('jesse in here now');
	if(grPort.next()){
	//gs.info('jesse1 ' + workflow.scratchpad.company);
	workflow.scratchpad.company.push(grPort.getValue('security_groups'));
	//gs.info('jesse2 ' + workflow.scratchpad.company);
	}
}
