activity.result  = ifreturncheck();

function ifreturncheck(){
	
	
	workflow.scratchpad.catitemtaskorder = workflow.scratchpad.catitemtaskorder+1;
	
	//workflow.info('workflow.scratchpad.catitemtaskorder='+workflow.scratchpad.catitemtaskorder);
	
	if(workflow.scratchpad.catitemtaskorder>workflow.scratchpad.catitemtaskordercount) return 0;
		else{
		var orders = workflow.scratchpad.catitemtaskorders;
		var catitemttask = new GlideRecord("x_sksw_uk_itsm_catalog_item_task");
		catitemttask.addEncodedQuery('active=true^sc_cat_item='+current.cat_item+'^order='+orders[workflow.scratchpad.catitemtaskorder]);
		catitemttask.query();
		while(catitemttask.next()){
			var sctask = new GlideRecord('sc_task');
			if(catitemttask.condition!=''){
				var match = GlideFilter.checkRecord(current,catitemttask.condition);
				if(match){
					sctask.initialize();
					sctask.request_item = current.sys_id.toString();
					sctask.description = catitemttask.description;
					sctask.short_description = catitemttask.short_description;
					sctask.assigned_to = catitemttask.assigned_to;
					sctask.assignment_group = catitemttask.assignment_group;
					sctask.x_sksw_uk_itsm_assignment_group = catitemttask.assignment_group;
					sctask.x_sksw_uk_itsm_uk_assigned_to = catitemttask.assigned_to;
					sctask.insert();
				}
			}else{
				sctask.initialize();
				sctask.request_item = current.sys_id.toString();
				sctask.description = catitemttask.description;
				sctask.short_description = catitemttask.short_description;
				sctask.assigned_to = catitemttask.assigned_to;
				sctask.assignment_group = catitemttask.assignment_group;
				sctask.x_sksw_uk_itsm_assignment_group = catitemttask.assignment_group;
				sctask.x_sksw_uk_itsm_uk_assigned_to = catitemttask.assigned_to;
				sctask.insert();
			}	
		}
		return 1;}
	}

activity.result  = ifreturncheck();

